Sql.query:
DECLARE p_typerecord CHAR(1) := :salsovteavtp; p_pericodides NUMBER := :pericodides; p_pericodiori NUMBER := :pericodiori; p_recacodi NUMBER := :recacodi; p_emprcodiori NUMBER := :emprcodiori; p_salsosaldoori NUMBER := :salsosaldoori; p_salsotipope NUMBER := :salsotipope; p_salsofecmigracion DATE := :salsofecmigracion; p_emprcodides NUMBER := :emprcodides; p_salsosaldodes NUMBER := :salsosaldodes; p_salsousucreacion VARCHAR2(60) := :salsousucreacion; p_salsousumodificacion VARCHAR2(60) := :salsousumodificacion; p_mensaje VARCHAR2(500) := NULL; p_salsocodi NUMBER := 0; p_count NUMBER := 0;
BEGIN
SELECT COUNT(salsocodi) INTO p_count FROM trn_saldos_sobrantes WHERE pericodides = p_pericodides AND pericodiori = p_pericodiori AND recacodi = p_recacodi AND salsovteavtp = p_typerecord AND emprcodiori =p_emprcodiori AND emprcodides = p_emprcodides;
IF p_count = 0 THEN SELECT nvl(MAX(salsocodi), 0) + 1 INTO p_salsocodi FROM trn_saldos_sobrantes;
INSERT INTO trn_saldos_sobrantes ( salsocodi, emprcodiori, pericodiori, recacodi, salsosaldoori, salsotipope, salsofecmigracion, pericodides, emprcodides, salsosaldodes, salsoestado, salsovteavtp, salsousucreacion, salsofeccreacion ) VALUES ( p_salsocodi, p_emprcodiori, p_pericodiori, p_recacodi, p_salsosaldoori, p_salsotipope, p_salsofecmigracion, p_pericodides, p_emprcodides, p_salsosaldodes, 'P', p_typerecord, p_salsousucreacion, sysdate );
ELSE
SELECT salsocodi INTO p_salsocodi FROM trn_saldos_sobrantes WHERE pericodides = p_pericodides AND pericodiori = p_pericodiori AND recacodi = p_recacodi AND salsovteavtp = p_typerecord AND emprcodiori =p_emprcodiori AND emprcodides = p_emprcodides;
UPDATE trn_saldos_sobrantes SET salsosaldoori = p_salsosaldoori, salsosaldodes = p_salsosaldodes, salsousumodificacion = p_salsousumodificacion, salsofecmodificacion = sysdate WHERE salsocodi = p_salsocodi;
END IF;
SELECT 'OK' INTO :mensaje FROM dual;
END;

DECLARE p_salsocodi NUMBER := :salsocodi; p_salsousumodificacion VARCHAR2(60) := :salsousumodificacion; p_emprcodiori NUMBER := 0; p_pericodiori NUMBER := 0; p_recacodi NUMBER := 0; p_pericodides NUMBER := 0; p_emprcodides NUMBER := 0; BEGIN SELECT emprcodiori, pericodiori, recacodi, pericodides, emprcodides INTO p_emprcodiori, p_pericodiori, p_recacodi, p_pericodides, p_emprcodides FROM trn_saldos_sobrantes WHERE salsocodi = p_salsocodi;
UPDATE trn_saldo_recalculo SET emprcodi = p_emprcodides WHERE emprcodi = p_emprcodiori AND pericodi = p_pericodiori AND recacodi = p_recacodi AND pericodidestino = p_pericodides;
UPDATE trn_saldos_sobrantes SET salsoestado = 'A', salsofecmodificacion = sysdate, salsofecproceso = sysdate, salsousumodificacion = p_salsousumodificacion WHERE salsocodi = p_salsocodi;
SELECT 'OK' INTO :mensaje FROM dual;
END;

DECLARE p_salsocodi NUMBER := :salsocodi; p_salsousumodificacion VARCHAR2(60) := :salsousumodificacion; p_emprcodiori NUMBER := 0; p_pericodiori NUMBER := 0; p_recacodi NUMBER := 0; p_pericodides NUMBER := 0; p_emprcodides NUMBER := 0; BEGIN SELECT emprcodiori, pericodiori, recacodi, pericodides, emprcodides INTO p_emprcodiori, p_pericodiori, p_recacodi, p_pericodides, p_emprcodides FROM trn_saldos_sobrantes WHERE salsocodi = p_salsocodi;
UPDATE vtp_saldo_empresa SET emprcodi = p_emprcodides WHERE emprcodi = p_emprcodiori AND pericodi = p_pericodiori AND recpotcodi = p_recacodi AND potsepericodidest = p_pericodides;
UPDATE trn_saldos_sobrantes SET salsoestado = 'A', salsofecmodificacion = sysdate, salsofecproceso = sysdate, salsousumodificacion = p_salsousumodificacion WHERE salsocodi = p_salsocodi;
SELECT 'OK VTP' INTO :mensaje FROM dual;
END;
