Sql.query:
DECLARE
P_IDCOPIA NUMBER :=:IDCOPIA;
P_GPSCODI_ORIGEN NUMBER :=:GPSCODI_ORIGEN;
P_GPSCODI_DESTINO NUMBER :=:GPSCODI_DESTINO;
P_FECHAHORA_INICIO  DATETIME :=:FECHAHORA_INICIO;
P_FECHAHORA_FIN  DATETIME :=:FECHAHORA_FIN;
P_MOTIVO VARCHAR2(400) :=:MOTIVO;
P_ESTADO VARCHAR2(1) :=:ESTADO;
P_USUCREACION VARCHAR2(20) :=:USUCREACION;
P_MENSAJE VARCHAR2(500):=NULL;
P_RESULTADO NUMBER:=0;

BEGIN

IF P_IDCOPIA = 0
THEN

SELECT NVL(MAX(IDCOPIA),0) INTO  P_RESULTADO FROM sic.f_copia_lectura;

INSERT INTO sic.f_copia_lectura
(IDCOPIA,
GPSCODI_ORIGEN,
GPSCODI_DESTINO,
FECHAHORA_INICIO,
FECHAHORA_FIN,
MOTIVO,
ESTADO,
FECCREACION,
USUCREACION
)
VALUES(P_RESULTADO +1
,P_GPSCODI_ORIGEN
,P_GPSCODI_DESTINO
,to_date(P_FECHAHORA_INICIO, 'DD/MM/YYYY HH24:MI:SS')
,to_date(P_FECHAHORA_FIN, 'DD/MM/YYYY HH24:MI:SS')
,P_MOTIVO
,P_ESTADO
,SYSDATE
,P_USUCREACION
);

END IF;

SELECT P_RESULTADO + 1 INTO :RESULTADO FROM DUAL;
END;

DECLARE P_INICIO DATE:= to_date('{0}', 'dd/MM/yyyy HH24:Mi:ss'); P_FIN DATE:= to_date('{1}', 'dd/MM/yyyy HH24:Mi:ss'); P_CODI_ORIGEN NUMBER :={2}; P_CODI_DESTINO NUMBER :={3}; P_USU VARCHAR2(60) := '{4}'; P_ID NUMBER:= 0; P_MENSAJE VARCHAR2(200); v_sql_query varchar2(2000); BEGIN select nvl((SELECT max(ID) FROM sic.FRECUENCIAS_AUDIT_CAB),0)+1 into P_ID from dual;
insert into sic.FRECUENCIAS_AUDIT_CAB(ID, GPSCODI, FECHAINICIAL, FECHAFINAL, USUARIO) values(P_ID, P_CODI_DESTINO, P_INICIO, P_FIN, P_USU);
insert into sic.FRECUENCIAS_AUDIT_DET(id, fecha, valor) select * from (select P_ID, to_date( to_char(FECHAHORA, 'dd/MM/yyyy HH24:mi') || decode(length(SEC), 2, replace(SEC, 'H', ':0'), replace(SEC, 'H', ':')), 'dd/MM/yyyy HH24:mi:ss') as FECHA, VALUE from( SELECT FECHAHORA, H0, H1, H2, H3, H4, H5, H6, H7, H8, H9, H10, H11, H12, H13, H14, H15, H16, H17, H18, H19, H20, H21, H22, H23, H24, H25, H26, H27, H28, H29, H30, H31, H32, H33, H34, H35, H36, H37, H38, H39, H40, H41, H42, H43, H44, H45, H46, H47, H48, H49, H50, H51, H52, H53, H54, H55, H56, H57, H58, H59 FROM sic.F_LECTURA WHERE GPSCODI = P_CODI_DESTINO AND FECHAHORA BETWEEN TRUNC(P_INICIO, 'MI') AND TRUNC(P_FIN, 'MI') ORDER BY FECHAHORA) unpivot( value for SEC in (H0, H1, H2, H3, H4, H5, H6, H7, H8, H9, H10, H11, H12, H13, H14, H15, H16, H17, H18, H19, H20, H21, H22, H23, H24, H25, H26, H27, H28, H29, H30, H31, H32, H33, H34, H35, H36, H37, H38, H39, H40, H41, H42, H43, H44, H45, H46, H47, H48, H49, H50, H51, H52, H53, H54, H55, H56, H57, H58, H59))) WHERE FECHA BETWEEN P_INICIO AND P_FIN;
UPDATE sic.FRECUENCIAS_AUDIT_CAB SET FILASAFECTADAS= (select count(1) from sic.FRECUENCIAS_AUDIT_DET where ID=P_ID) WHERE ID=P_ID;
FOR A IN (select 'UPDATE sic.F_LECTURA set '||a||' where FECHAHORA=TO_DATE('''||f||''',''DD/MM/YYYY HH24:mi'') and GPSCODI='||gpscodi S from ( select id,to_char(fecha,'DD/MM/YYYY HH24:mi')F ,LISTAGG('H'||EXTRACT(SECOND FROM CAST(FECHA AS TIMESTAMP))||'=NULL',',')a from sic.FRECUENCIAS_AUDIT_DET B where ID=P_ID group by id,to_char(fecha,'DD/MM/YYYY HH24:mi'))a inner join sic.FRECUENCIAS_AUDIT_CAB b on a.id=b.id) LOOP v_sql_query:=A.S; EXECUTE IMMEDIATE v_sql_query; END LOOP;
update SIC.F_LECTURA dest set dest.MINIMO = null,dest.MAXIMO=null,dest.VSF =null,dest.DESV =null,dest.NUM=0 where exists (select 1 from sic.FRECUENCIAS_AUDIT_CAB a where a.GPSCODI = dest.GPSCODI and id=P_ID and dest.FECHAHORA between TRUNC(a.FECHAINICIAL, 'MI') and TRUNC(a.FECHAFINAL, 'MI'));
MERGE INTO SIC.F_LECTURA dest USING (select FECHAHORA,GPSCODI,max(VALUE)MAXIMO, min(VALUE)MINIMO, round(POWER(SUM(POWER(VALUE, 2))/count(1), 0.5)-60,5) VSF,round((sum(value)-(count(1)*60))/60,5) DESV,count(VALUE) NUM from(select l.* from SIC.F_LECTURA l inner join sic.FRECUENCIAS_AUDIT_CAB a on a.GPSCODI = l.GPSCODI where id=P_ID and l.FECHAHORA between TRUNC(a.FECHAINICIAL, 'MI') and TRUNC(a.FECHAFINAL, 'MI') ORDER BY FECHAHORA) unpivot( value for SEC in (H0, H1, H2, H3, H4, H5, H6, H7, H8, H9, H10, H11, H12, H13, H14, H15, H16, H17, H18, H19, H20, H21, H22, H23, H24, H25, H26, H27, H28, H29, H30, H31, H32, H33, H34, H35, H36, H37, H38, H39, H40, H41, H42, H43, H44, H45, H46, H47, H48, H49, H50, H51, H52, H53, H54, H55, H56, H57, H58, H59)) group by FECHAHORA,GPSCODI) orig ON (dest.FECHAHORA = orig.FECHAHORA and dest.GPSCODI = orig.GPSCODI) WHEN MATCHED THEN UPDATE SET dest.MINIMO = orig.MINIMO, dest.MAXIMO = orig.MAXIMO, dest.VSF = orig.VSF, dest.DESV = orig.DESV, dest.NUM=orig.NUM; COMMIT;
END;

DECLARE
P_IDCARGA NUMBER :=:IDCARGA;
P_FECHAHORA varchar2(19) :=:FECHAHORA;
P_FRECUENCIA NUMBER :=:FRECUENCIA;
P_TENSION  NUMBER :=:TENSION;

P_MENSAJE VARCHAR2(500):=NULL;
P_CODMAX NUMBER:=0;

BEGIN

INSERT INTO sic.f_lectura_virtual
(IDCARGA,
FECHAHORA,
FRECUENCIA,
TENSION
)
VALUES(P_IDCARGA
,P_FECHAHORA
,P_FRECUENCIA
,P_TENSION);

SELECT P_MENSAJE INTO :MENSAJE FROM DUAL;
END;

DECLARE
P_IDCOPIA NUMBER :=:IDCOPIA;
P_ESTADO VARCHAR2(1) :=:GPSESTADO;
P_USUCREACION VARCHAR2(20) :=:USUCREACION;
P_MENSAJE VARCHAR2(500):=NULL;

BEGIN

UPDATE sic.f_copia_lectura
SET
ESTADO=P_ESTADO,
FECELIMINA=SYSDATE,
USUELIMINA=P_USUCREACION
WHERE IDCOPIA=P_IDCOPIA;


SELECT P_MENSAJE INTO :MENSAJE FROM DUAL;
END;
