WITH equipo AS ( SELECT eq.* FROM eq_equipo eq join epr_equipo ep on eq.equicodi = ep.equicodi and ep.epequiestregistro='1' WHERE eq.famcodi = 8 AND ( nvl(:equicodi, 0) = 0 OR eq.equicodi = :equicodi ) AND ( TRIM(:codigo) IS NULL OR upper(eq.equiabrev) LIKE '%'|| upper(:codigo)|| '%' ) AND ( nvl(:emprcodi, 0) = 0 OR eq.emprcodi = :emprcodi ) AND ( TRIM(:equiestado) IS NULL OR eq.equiestado = :equiestado ) ) , ult_fecha_prop AS ( SELECT p.equicodi, p.propcodi, MAX(p.fechapropequi) AS fechapropequi FROM eq_propequi p JOIN equipo e ON p.equicodi = e.equicodi AND p.propequideleted=0 GROUP BY p.equicodi, p.propcodi ) , ult_fecha_modificacion_equipo AS ( SELECT p.equicodi, MAX(p.fechapropequi) AS fechapropequi FROM ult_fecha_prop p GROUP BY p.equicodi ) , ult_modificador_equipo AS ( SELECT p.equicodi ,MAX(nvl(p.propequiusumodificacion,p.propequiusucreacion)) AS propequiusumodificacion ,MAX(nvl(p.propequifecmodificacion,p.propequifeccreacion)) AS propequifecmodificacion FROM eq_propequi p JOIN ult_fecha_modificacion_equipo e ON p.equicodi = e.equicodi AND p.fechapropequi = e.fechapropequi AND p.propequideleted=0 GROUP BY p.equicodi ) ,data_prop AS ( SELECT pe.equicodi, pe.propcodi, pe.valor, pe.propequicomentario FROM eq_propequi pe JOIN ult_fecha_prop up ON pe.equicodi = up.equicodi AND pe.propcodi = up.propcodi AND pe.fechapropequi = up.fechapropequi AND pe.propequideleted=0 ) ,eq_propiedad AS ( SELECT * FROM data_prop PIVOT ( MAX(valor) ,max(propequicomentario) as coment FOR propcodi IN ( 3351 as id_area ,1852 as tension ,699 as capacidad_a ,3352 as capacidad_mva ,3353 as capacidad_transmision_a ,3354 as capacidad_transmision_mva ,3355 as id_celda_1 ,3356 as id_celda_2 ,3357 as id_banco_condensador ,3358 as capac_trans_cond_1_porcen ,3359 as capac_trans_cond_1_min ,3361 as capac_trans_cond_2_porcen ,3362 as capac_trans_cond_2_min ,3366 as factor_limitante_final
) ) ) SELECT e.equicodi as equicodi ,al.areanomb as AREANOMB ,em.emprnomb as emprnomb ,e.equiabrev as equiabrev ,p.tension ,ac1.areanomb as subestacion_1 ,ac2.areanomb as subestacion_2 ,p.capacidad_transmision_a as capacidad_a ,p.capacidad_transmision_mva as capacidad_mva ,p.capac_trans_cond_1_porcen ,p.capac_trans_cond_1_min ,p.capac_trans_cond_2_porcen ,p.capac_trans_cond_2_min ,p.factor_limitante_final ,es.eqcatddescripcion as estado ,e.equiestado ,uum.propequiusumodificacion as USUARIO_AUDITORIA ,TO_CHAR(uum.propequifecmodificacion, 'DD/MM/YYYY HH24:MI') as FECHA_MODIFICACIONSTR -- nuevos campos ,p.capacidad_transmision_a_coment ,p.capacidad_transmision_mva_coment ,p.capac_trans_cond_1_porcen_coment ,p.capac_trans_cond_1_min_coment ,p.capac_trans_cond_2_porcen_coment ,p.capac_trans_cond_2_min_coment ,p.factor_limitante_final_coment
FROM equipo e LEFT JOIN eq_propiedad p ON p.equicodi = e.equicodi LEFT JOIN ult_fecha_modificacion_equipo ufm ON p.equicodi = ufm.equicodi LEFT JOIN ult_modificador_equipo uum ON p.equicodi = uum.equicodi LEFT JOIN eq_area al on to_number(p.id_area default 0 on conversion error) = al.areacodi LEFT JOIN si_empresa em on e.emprcodi = em.emprcodi LEFT JOIN eq_equipo ec1 on to_number(p.id_celda_1 default 0 on conversion error) = ec1.equicodi LEFT JOIN eq_area ac1 on ec1.areacodi= ac1.areacodi LEFT JOIN eq_equipo ec2 on to_number(p.id_celda_2 default 0 on conversion error) = ec2.equicodi LEFT JOIN eq_area ac2 on ec2.areacodi= ac2.areacodi LEFT JOIN eq_prop_catalogo_data es ON eqcatccodi = 6 and e.equiestado = es.eqcatdabrev WHERE 1 = 1 AND ( nvl(:id_subestacion_1, 0) = 0 OR ec1.areacodi = :id_subestacion_1 ) AND ( nvl(:id_subestacion_2, 0) = 0 OR ec2.areacodi = :id_subestacion_2 ) AND ( nvl(:id_area, 0) = 0 OR to_number(p.id_area default 0 on conversion error) = :id_area ) AND ( nvl(:tension, 0) = 0 OR to_number(p.tension default 0 on conversion error) = :tension )

