Sql.query:
select empgcodi as EmpgcodiCal, GEMP.emprcodi as EmprcodiCal, (select pericodi from gmm_periodo where perianio = {0} and perimes = {1}) as pericodical, nvl(pericodi, 0) as Pericodi, case when ( SELECT COUNT(*) from gmm_datcalculo dat2 WHERE dat2.DCALANIO = {0} and dat2.DCALMES = {1} and dat2.TINSCODI = 'SSCC' and dat2.DATCALESTADO ='1' AND DAT2.EMPGCODI=GEMP.EMPGCODI ) = 1 then '1' else '0' end as Empgfasecal,
(select periestado from gmm_periodo where perianio = {0} and perimes = {1}) as periestado
from gmm_empresa GEMP where empgestado = 'H' and '{2}' >= to_char(gemp.empgfecingreso,'YYYYMM')

select empgcodi as EmpgcodiCal, GEMP.emprcodi as EmprcodiCal, (select pericodi from gmm_periodo where perianio = {0} and perimes = {1}) as pericodical, nvl(pericodi, 0) as Pericodi, case when ( SELECT COUNT(*) from gmm_datcalculo dat2 WHERE dat2.DCALANIO = {0} and dat2.DCALMES = {1} and dat2.TINSCODI = 'ENTPRE' and dat2.DATCALESTADO ='1' AND DAT2.EMPGCODI=GEMP.EMPGCODI ) = 1 then '1' else '0' end as Empgfasecal,
(select periestado from gmm_periodo where perianio = {0} and perimes = {1}) as periestado
from gmm_empresa GEMP where empgestado = 'H' and '{2}' >= to_char(gemp.empgfecingreso,'YYYYMM')
