Sql.query:
select t.* from ( select emp.emprnomb, trim(eqp.equinomb) equinomb, eq.equiabrev gruponomb, eq.equicodi, eq.famcodi, emp.emprcodi, eq.grupocodi,eqp.equicodi Centralid, 0 grupopadre, f.tgenercodi from eq_equipo eq inner join eq_equipo eqp on eqp.equicodi = eq.equipadre inner join si_empresa emp on emp.emprcodi = eq.emprcodi and emp.EMPRESTADO = 'A' left join pr_grupo gr on gr.grupocodi = eq.grupocodi left join SI_FUENTEENERGIA f on f.fenergcodi = gr.fenergcodi where eq.famcodi = 3 and eq.equiestado = 'A' and eq.equipadre not in ( select ce.equicodi from pr_grupo gr inner join ( select pr.grupopadre, max(pr.grupocodi) grupocodi from pr_grupo pr inner join pr_grupo pr1 on pr1.grupocodi = pr.grupopadre and pr1.catecodi = 3 and pr1.grupoactivo = 'S' where pr.grupopadre in (select pg.grupopadre idtv from pr_grupo pg inner join pr_grupo pgp on pgp.grupocodi = pg.grupopadre inner join pr_grupocc cc on cc.grupocodicc = pg.grupocodi where pg.GRUPOACTIVO ='S' group by pg.grupopadre) group by pr.grupopadre) gr2 on gr2.grupocodi = gr.grupocodi inner join eq_equipo eq on eq.grupocodi = gr.grupopadre inner join eq_equipo ce on ce.equicodi = eq.equipadre inner join si_empresa emp on emp.emprcodi = gr.emprcodi where gr.grupoactivo = 'S' )
UNION
select emp.emprnomb, trim(ce.equinomb) equinomb, gr.gruponomb, ce.equicodi ,eq.famcodi, emp.emprcodi, gr.grupocodi, ce.equicodi Centralid, gr.grupopadre, f.tgenercodi from pr_grupo gr inner join (
--Modos de operaciï¿½n con mayor potencia efectiva por central select * from ( select idtv,grupocodi,valor, ROW_NUMBER() OVER(PARTITION BY idtv ORDER BY idtv,valor DESC) AS NUM from( select idtv,grupocodi,valor,count(*) tot from ( select eq.equipadre, pg.grupopadre idtv, eq.equicodi,eq.equinomb, pg.grupocodi, pg.gruponomb, coalesce(pot_efe.valor,0) valor from pr_grupo pg inner join pr_grupo pgp on pgp.grupocodi = pg.grupopadre inner join pr_grupocc cc on cc.grupocodicc = pg.grupocodi inner join eq_equipo eq on eq.grupocodi = cc.grupocodidet and eq.equiestado = 'A' left join ( select grupocodi, TO_NUMBER(REPLACE( VALOR, '.', ','), '999999999999D999999999999', 'NLS_NUMERIC_CHARACTERS='',.''') valor from ( select distinct eq.equicodi, gr.grupocodi, gr.gruponomb, cnp.concepcodi,cnp.concepabrev,dat.formuladat ,trim(coalesce(REGEXP_SUBSTR(trim(coalesce(dat.formuladat,'-1')), '^(-|+)?\d+(\.|,)?(\d+)?$'),'-1')) valor ,cnp.concepunid,dat.fechadat, gr.EMPRCODI from pr_grupodat dat, pr_concepto cnp, pr_grupo gr, eq_equipo eq, (select grupocodi,concepcodi, max(fechadat) as fechamax from pr_grupodat where deleted = 0 and fechadat <= sysdate group by grupocodi,concepcodi) vi where dat.deleted = 0 and cnp.concepcodi in (14) and dat.grupocodi = vi.grupocodi and eq.grupocodi = gr.grupopadre and dat.concepcodi= vi.concepcodi and dat.fechadat=vi.fechamax and cnp.concepcodi=dat.concepcodi and gr.grupocodi = vi.grupocodi and gr.catecodi = 2 and gr.grupoactivo = 'S' order by 1 asc ) )pot_efe on pot_efe.grupocodi = pg.grupocodi where pg.GRUPOACTIVO ='S' )group by idtv,grupocodi,valor )a ) where num=1
) gr2 on gr2.grupocodi = gr.grupocodi inner join eq_equipo eq on eq.grupocodi = gr.grupopadre inner join eq_equipo ce on ce.equicodi = eq.equipadre inner join si_empresa emp on emp.emprcodi = gr.emprcodi and emp.EMPRESTADO = 'A' left join SI_FUENTEENERGIA f on f.fenergcodi = gr.fenergcodi where gr.grupoactivo = 'S'
) t where t.centralid <> 13335 --tablazo and t.centralid <> 1213 --ilo1 order by t.emprnomb, t.equinomb, t.gruponomb
