SELECT exte.* FROM (SELECT ROWNUM AS ITEM , inte.* FROM ( SELECT pees.PERIODO AS PERIODO , CASE WHEN medi.MEDIFECHA IS NULL THEN CASE WHEN tiem.TIPOEMPRCODI = 2 THEN CASE WHEN median.MEDIFECHA IS NULL THEN '[Sin datos PR16]' ELSE 'PR16: ' || TO_CHAR(median.MEDIFECHA, 'MM YYYY') END WHEN tiem.TIPOEMPRCODI = 4 THEN CASE WHEN median.MEDIFECHA IS NULL THEN '[Sin datos Sicli ' || pesic.PSICLIANIOMESPERREM || ']' ELSE 'Sicli ' || pesic.PSICLIANIOMESPERREM || ': ' || TO_CHAR(median.MEDIFECHA, 'MM YYYY') END ELSE NULL END ELSE pees.PERIODO END AS FUENTE , pees.FECHA_FILA , CASE WHEN ulenv.ENVIOFECHA_MAX BETWEEN pees.INI_REMISION AND pees.FIN_REMISION THEN '1' WHEN ulenv.ENVIOFECHA_MAX > pees.FIN_REMISION THEN '2' WHEN pees.INI_REMISION > ulenv.ENVIOFECHA_MAX THEN '3' WHEN ulenv.ENVIOFECHA_MAX IS NULL THEN '4' ELSE '5' END AS CUMPLIMIENTO , eqp.OSINERGCODI AS CODIGO_CLIENTE , NVL(mrem.EMPRRAZSOCIAL, ' - SIN RAZON SOCIAL - ') AS SUMINISTRADOR , empr.EMPRCODI , empr.EMPRRUC AS RUC_EMPRESA , NVL(empr.EMPRRAZSOCIAL, ' - SIN RAZON SOCIAL - ') AS NOMBRE_EMPRESA ,CASE empr.TIPOEMPRCODI WHEN 2 THEN 'ED - SEIN' WHEN 4 THEN ara.AREANOMB END AS SUBESTACION , CASE empr.TIPOEMPRCODI WHEN 2 THEN 'ED - SEIN' WHEN 4 THEN TRIM(TO_CHAR(eqp.EQUITENSION, '99,999.99')) END AS TENSION , CASE WHEN medi.MEDIFECHA IS NULL THEN median.MEDIFECHA ELSE medi.MEDIFECHA END AS MEDIFECHA , CASE WHEN medi.MEDIFECHA IS NULL THEN median.MEDITOTAL ELSE medi.MEDITOTAL END AS MEDITOTAL , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H1 ELSE medi.H1 END AS H1 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H2 ELSE medi.H2 END AS H2 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H3 ELSE medi.H3 END AS H3 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H4 ELSE medi.H4 END AS H4 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H5 ELSE medi.H5 END AS H5 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H6 ELSE medi.H6 END AS H6 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H7 ELSE medi.H7 END AS H7 , CASE WHEN medi.MEDIFECHA