S NULL THEN median.H87 ELSE medi.H87 END AS H87 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H88 ELSE medi.H88 END AS H88 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H89 ELSE medi.H89 END AS H89 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H90 ELSE medi.H90 END AS H90 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H91 ELSE medi.H91 END AS H91 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H92 ELSE medi.H92 END AS H92 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H93 ELSE medi.H93 END AS H93 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H94 ELSE medi.H94 END AS H94 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H95 ELSE medi.H95 END AS H95 , CASE WHEN medi.MEDIFECHA IS NULL THEN median.H96 ELSE medi.H96 END AS H96 , ulenv.ENVIOCODI_CONT AS NRO_ENVIOS , ulenv.ENVIOFECHA_MIN AS FECHA_PRIMER_ENVIO , ulenv.ENVIOFECHA_MAX AS FECHA_ULTIMO_ENVIO , pees.INI_REMISION , pees.FIN_REMISION , pees.INI_PERIODO FROM ( SELECT TO_CHAR(P.FECHA,'MM YYYY') AS PERIODO, TO_DATE('{1}','DD/MM/YYYY') AS INI_PERIODO ,ADD_MONTHS(TO_DATE('{1}','DD/MM/YYYY'),F.FORMATMESPLAZO)+F.FORMATDIAPLAZO+F.FORMATMINPLAZO/(24*60) AS INI_REMISION ,ADD_MONTHS(TO_DATE('{1}','DD/MM/YYYY'),F.FORMATMESFINPLAZO)+F.FORMATDIAFINPLAZO+F.FORMATMINFINPLAZO/(24*60) AS FIN_REMISION ,P.FECHA AS FECHA_FILA FROM ME_FORMATO F ,(SELECT TO_DATE('{1}','DD/MM/YYYY')+ROWNUM-1 AS FECHA FROM DUAL CONNECT BY LEVEL <= (SELECT LAST_DAY(TO_DATE('{1}','DD/MM/YYYY'))- TO_DATE('{1}','DD/MM/YYYY') FROM DUAL) +1 ) P WHERE F.FORMATCODI = {0} ORDER BY 5 ) pees INNER JOIN ME_FORMATO form ON (form.FORMATCODI = {0}) INNER JOIN ME_PTOMEDICION ptme ON (ptme.ORIGLECTCODI = 19) inner join ( --Puntos de medicion activos hasta de la Fecha de Inicio SELECT * FROM ( SELECT pto .Ptomedicodi, pto.codref, pto.Origlectcodi, pto.Equicodi, pto.Ptomedielenomb, pto.Ptomediestado,empr.emprcodi,empr.emprnomb, vwpt.HPTDATFECHA, vwpt.HPTDATPTOESTADO, ROW_NUMBER() OVER(PARTITION BY pto.PTOMEDICODI,empr.emprcodi ORDER BY pto.PTOMEDICODI,empr.emprcodi,vwpt.HPTDATFECHA desc, vwpt.HPTDATPTOESTADO desc) AS NUM_VIEW FROM ME_PTOMEDICION pto inner join SI_HISEMPPTO_DATA vwpt on vwpt.HPTDATFECHA <= TO_DATE('{1}', 'dd/mm/yyyy') and (vwpt.PTOMEDICODI = pto.PTOMEDICODI OR vwpt.PTOMEDICODIOLD = pto.PTOMEDICODI) INNER JOIN SI_EMPRESA empr ON empr.emprcodi = vwpt.emprcodi )empr WHERE NUM_VIEW = 1 and HPTDATPTOESTADO = 'I' and {3} and ORIGLECTCODI = 19
UNION ALL
--Puntos de medicion activos despues de la Fecha de Inicio SELECT * FROM ( SELECT pto .Ptomedicodi, pto.codref, pto.Origlectcodi, pto.Equicodi, pto.Ptomedielenomb, pto.Ptomediestado,empr.emprcodi,empr.emprnomb, vwpt.HPTDATFECHA, vwpt.HPTDATPTOESTADO, ROW_NUMBER() OVER(PARTITION BY pto.PTOMEDICODI,empr.emprcodi ORDER BY pto.PTOMEDICODI,empr.emprcodi,vwpt.HPTDATFECHA desc, vwpt.HPTDATPTOESTADO desc) AS NUM_VIEW FROM ME_PTOMEDICION pto inner join SI_HISEMPPTO_DATA vwpt on vwpt.HPTDATFECHA > TO_DATE('{1}', 'dd/mm/yyyy') and vwpt.HPTDATFECHA <= (ADD_MONTHS(TO_DATE('{1}', 'dd/mm/yyyy'),1) - 1) and (vwpt.PTOMEDICODI = pto.PTOMEDICODI OR vwpt.PTOMEDICODIOLD = pto.PTOMEDICODI) INNER JOIN SI_EMPRESA empr ON empr.emprcodi = vwpt.emprcodi )empr WHERE NUM_VIEW = 1 and HPTDATPTOESTADO = 'I' and {3} and ORIGLECTCODI = 19 )vwpt on ptme.ptomedicodi = vwpt.ptomedicodi INNER JOIN SI_EMPRESA empr ON {3} and empr.TIPOEMPRCODI IN (2, 4) and vwpt.emprcodi = empr.emprcodi INNER JOIN SI_TIPOEMPRESA tiem ON (empr.TIPOEMPRCODI = tiem.TIPOEMPRCODI) INNER JOIN (SELECT pesici.PSICLICODI , pesici.PSICLIANIOMESPERREM FROM IIO_PERIODO_SICLI pesici WHERE pesici.PSICLICODI = {2} UNION ALL SELECT 0 PSICLICODI , 'AAAA MM' PSICLIANIOMESPERREM FROM dual dua WHERE 2 = {9}) pesic ON (1 = 1) LEFT JOIN ( SELECT r1.PTOMEDICODI , r1.PTOSUFECHAINICIO , r2.PTOSUFECHAINICIO AS PTOSUFECHAFIN , r1.EMPRCODI FROM (SELECT ROWNUM SEC, T.* FROM (SELECT PTOMEDICODI , PTOSUFECHAINICIO , EMPRCODI FROM VW_PTOSUMINISTRADOR ORDER BY 1,2) T) r1 LEFT JOIN (SELECT ROWNUM SEC, T.* FROM (SELECT PTOMEDICODI , PTOSUFECHAINICIO , EMPRCODI FROM VW_PTOSUMINISTRADOR ORDER BY 1,2) T) r2 ON (r1.PTOMEDICODI = r2.PTOMEDICODI AND (r1.SEC + 1) = r2.SEC) ) mrsu ON (ptme.PTOMEDICODI = mrsu.PTOMEDICODI AND pees.INI_PERIODO >= mrsu.PTOSUFECHAINICIO AND pees.INI_PERIODO < NVL(mrsu.PTOSUFECHAFIN, SYSDATE) ) LEFT JOIN SI_EMPRESA mrem ON (mrsu.EMPRCODI = mrem.EMPRCODI) LEFT JOIN (SELECT enul.FORMATCODI , enul.EMPRCODI , enul.ENVIOCODI_MAX , enul.ENVIOCODI_CONT , TRUNC(enpri.ENVIOFECHA) AS ENVIOFECHA_MIN , TRUNC(enuld.ENVIOFECHA) AS ENVIOFECHA_MAX , enuld.ENVIOFECHAPERIODO , enuld.ENVIOFECHAINI , enuld.ENVIOFECHAFIN FROM (SELECT envi.FORMATCODI , envi.EMPRCODI , envi.ENVIOFECHAPERIODO , MIN(envi.ENVIOCODI) AS ENVIOCODI_MIN , MAX(envi.ENVIOCODI) AS ENVIOCODI_MAX , COUNT(envi.ENVIOCODI) AS ENVIOCODI_CONT FROM ME_ENVIO envi WHERE envi.FORMATCODI = {0} GROUP BY envi.FORMATCODI , envi.EMPRCODI , envi.ENVIOFECHAPERIODO) enul INNER JOIN ME_ENVIO enpri ON (enul.ENVIOCODI_MIN = enpri.ENVIOCODI) INNER JOIN ME_ENVIO enuld ON (enul.ENVIOCODI_MAX = enuld.ENVIOCODI) ) ulenv ON (form.FORMATCODI = ulenv.FORMATCODI AND empr.EMPRCODI = ulenv.EMPRCODI AND pees.INI_PERIODO = TRUNC(ulenv.ENVIOFECHAPERIODO, 'MM')) LEFT JOIN ME_MEDICION96 medi ON (medi.LECTCODI = {6} AND TRUNC(pees.FECHA_FILA) = TRUNC(medi.MEDIFECHA) AND ptme.PTOMEDICODI = medi.PTOMEDICODI ) LEFT JOIN ME_MEDICION96 median ON (median.LECTCODI = CASE WHEN tiem.TIPOEMPRCODI = 2 THEN {6} WHEN tiem.TIPOEMPRCODI = 4 THEN {7} ELSE NULL END AND TRUNC(median.MEDIFECHA) = FN_ME_FECHAMEDSICLI(ptme.PTOMEDICODI , tiem.TIPOEMPRCODI , pees.FECHA_FILA , medi.MEDIFECHA , pesic.PSICLICODI , {7} ) AND ptme.PTOMEDICODI = median.PTOMEDICODI ) LEFT JOIN EQ_EQUIPO eqp ON (ptme.EQUICODI = eqp.EQUICODI) LEFT JOIN EQ_AREA ara ON (eqp.AREACODI = ara.AREACODI) WHERE {3} {4} AND TRUNC(pees.INI_PERIODO) = TO_DATE('{1}', 'dd/mm/yyyy') AND ( EXISTS ( SELECT 1 FROM ME_MEDICION96 mpr16 WHERE mpr16.LECTCODI = {6} AND TRUNC(mpr16.MEDIFECHA) = TRUNC(pees.FECHA_FILA) AND mpr16.PTOMEDICODI = ptme.PTOMEDICODI ) OR EXISTS ( SELECT 1 FROM EQ_EQUIPO eqpsi , IIO_FACTURA factu WHERE eqpsi.EQUICODI = ptme.EQUICODI AND eqpsi.EQUICODI = factu.EQUICODI AND factu.PSICLICODI = pesic.PSICLICODI AND CASE WHEN tiem.TIPOEMPRCODI = 2 THEN 0 WHEN tiem.TIPOEMPRCODI = 4 THEN 1 END = 1 ) ) {5} ORDER BY pees.INI_PERIODO , empr.EMPRNOMB , ptme.PTOMEDICODI , pees.FECHA_FILA , medi.MEDIFECHA ) inte) exte {8}

select
*
from
me_medicion96
where
medifecha between to_date('{0}','YYYY-MM-DD') and to_date('{1}','YYYY-MM-DD')
and lectcodi = {2}
and tipoinfocodi ={3}
and ptomedicodi={4}

select * from( select b.ptomedicodi,d.equicodi idCentral,d.equinomb central,c.equinomb GrupSSAA,g.tgenercodi tipoGeneracion, sum(a.meditotal)/4 total from me_medicion96 a inner join me_ptomedicion b on a.ptomedicodi = b.ptomedicodi inner join eq_equipo c on b.equicodi = c.equicodi inner join eq_equipo d on c.equipadre = d.equicodi inner join pr_grupo e on c.grupocodi = e.grupocodi inner join SI_FUENTEENERGIA f on e.fenergcodi = f.fenergcodi inner join SI_TIPOGENERACION g on f.tgenercodi = g.tgenercodi where (case when coalesce(a.EMPRCODI, -1) > 0 then a.EMPRCODI else c.emprcodi end) = {0} and (a.medifecha >= to_date('{3}','yyyy-MM-dd') and a.medifecha <= to_date('{4}','yyyy-MM-dd')) and a.LECTCODI = {1} and a.tipoinfocodi = {2} group by b.ptomedicodi,d.equicodi,d.equinomb,c.equinomb,g.tgenercodi union
select b.ptomedicodi,e.equicodi idCentral, e.equinomb central,c.equinomb GrupSSAA,d.TIPORELCODI tipoGeneracion,sum(a.meditotal)/4 total from me_medicion96 a,me_ptomedicion b, eq_equipo c, EQ_EQUIREL d,eq_equipo e where a.lectcodi = 1 and a.tipoinfocodi = 1 and a.ptomedicodi = b.ptomedicodi and b.origlectcodi = 14 and b.equicodi = c.equicodi and c.equicodi = d.equicodi1 and d.equicodi2 = e.equicodi and c.emprcodi = {0} and (a.medifecha >= to_date('{3}','yyyy-MM-dd') and a.medifecha <= to_date('{4}','yyyy-MM-dd')) group by b.ptomedicodi, e.equinomb,c.equinomb,d.TIPORELCODI,e.equicodi
) order by tipoGeneracion,ptomedicodi

delete from me_medicion96 where lectcodi = {0} and Tptomedicodi = {5} and medifecha >= to_date('{1}','YYYY-MM-DD') and
medifecha <= to_date('{2}','YYYY-MM-DD') and ptomedicodi in (
select distinct hp.ptomedicodi from me_hojaptomed hp
inner join me_ptomedicion pto on pto.ptomedicodi = hp.ptomedicodi
where hp.formatcodi = {3} and pto.emprcodi = {4}
)
