Sql.query:
DECLARE
P_CAGRUSERREGI  VARCHAR2(200):=:TrnPctUsernameIns;
p_codigoAgrupado NUMBER(20):=0;
BEGIN
INSERT INTO TRN_POTENCIA_CONTRATADA_AGR (PCAGRUSERREGI,PCAGRFECREGI,PCAGRESTADO ) VALUES(P_CAGRUSERREGI,SYSDATE,'ACT') returning TRNPCAGRP INTO p_codigoAgrupado;
select p_codigoAgrupado INTO :CodigoAgrupado from dual;
END;

DECLARE
P_PERICODI   NUMBER:=:PERICODI;
p_coresocodiAux number := :CoresoCodi;
p_coregecodiAux number := :CoregeCodi;
p_coresocodiAuxFirst number := :CoresoCodiFirst;
p_coregecodiAuxFirst number := :CoregeCodiFirst;
p_numeroOrden   number := :TrnpcNumord;
p_codigoAgrupado number:= :TrnpCagrp;
p_tipoCasoAgrupado varchar2(10):=:TrnnpcCodicas;
p_codigoCasoAgrupado number:=0;

BEGIN


SELECT  max(trnpcagrp)   INTO p_codigoAgrupado
FROM   TRN_POTENCIA_CONTRATADA
WHERE CORESOCODI=p_coresocodiAuxFirst
AND (coregecodi=p_coregecodiAuxFirst OR p_coregecodiAuxFirst IS NULL)
AND TRNPCNUMORD=1
AND  TRNPCESTADO='ACT'
AND NVL(PERIDCCODI,PERICODI)=p_PERICODI;

IF(p_codigoAgrupado IS NULL)
THEN
SELECT  NVL(MAX(trnpcagrp),0)+1  INTO p_codigoAgrupado
FROM   TRN_POTENCIA_CONTRATADA;
-- WHERE CORESOCODI=p_coresocodiAux
--and coregecodi=p_coregecodiAux
-- AND  TRNPCESTADO='ACT';
END IF;



SELECT TIPCASCODI INTO p_codigoCasoAgrupado
FROM VTP_TIPO_CASO_AGR
WHERE  TIPCASABREV =p_tipoCasoAgrupado;

UPDATE TRN_POTENCIA_CONTRATADA
SET  TRNPCAGRP=p_codigoAgrupado
,TRNPCNUMORD= p_numeroOrden
,TRNPCCODICAS=p_codigoCasoAgrupado
,TrnPctUserNameIns ='testMDP'||TO_CHAR(sysdate, 'hh:mi')
WHERE CORESOCODI=p_coresocodiAux
AND (coregecodi=p_coregecodiAux OR p_coregecodiAux IS NULL)
AND TRNPCESTADO='ACT'
AND NVL(PERIDCCODI,PERICODI)=p_PERICODI;

SELECT p_codigoAgrupado INTO :CodigoAgrupado from dual;
END;

DECLARE P_PERICODI NUMBER:=:PERICODI; p_coresocodiAux number := :CoresoCodi; p_coregecodiAux number := :CoregeCodi; p_codigoAgrupado NUMBER:=0; p_tipoCasoAgrupado varchar2(10):= :TrnnpcCodicas; p_omitirCargaExcel NUMBER :=:OmitirExcel; BEGIN
IF(p_tipoCasoAgrupado='AGRVTP') THEN SELECT TRNPCAGRP INTO p_codigoAgrupado FROM TRN_POTENCIA_CONTRATADA WHERE TRNPCAGRP IS NOT NULL AND CORESOCODI=p_coresocodiAux AND COREGECODI=p_coregecodiAux AND TRNPCESTADO='ACT' AND NVL(PERIDCCODI,PERICODI)=p_PERICODI; END IF;
IF(p_tipoCasoAgrupado='AGRVTA') THEN SELECT NVL( ( SELECT DISTINCT TRNPCAGRP FROM TRN_POTENCIA_CONTRATADA WHERE TRNPCAGRP IS NOT NULL AND CORESOCODI=p_coresocodiAux AND TRNPCESTADO='ACT' AND NVL(PERIDCCODI,PERICODI)=p_PERICODI ),-1) INTO p_codigoAgrupado FROM DUAL;
END IF;
IF(p_omitirCargaExcel=0) THEN
IF(NVL(p_codigoAgrupado,0)>0) THEN UPDATE TRN_POTENCIA_CONTRATADA SET TRNPCAGRP=null, TRNPCNUMORD=null, TRNPCCODICAS =null, TRNPCTTOTALMWFIJA=CASE WHEN TRNPCEXCEL=1 THEN NULL ELSE TRNPCTTOTALMWFIJA END, TRNPCTHPMWFIJA=CASE WHEN TRNPCEXCEL=1 THEN NULL ELSE TRNPCTHPMWFIJA END, TRNPCTHFPMWFIJA=CASE WHEN TRNPCEXCEL=1 THEN NULL ELSE TRNPCTHFPMWFIJA END, TRNPCTTOTALMWVARIABLE=CASE WHEN TRNPCEXCEL=1 THEN NULL ELSE TRNPCTTOTALMWVARIABLE END, TRNPCTHPMWFIJAVARIABLE=CASE WHEN TRNPCEXCEL=1 THEN NULL ELSE TRNPCTHPMWFIJAVARIABLE END, TRNPCTHFPMWFIJAVARIABLE=CASE WHEN TRNPCEXCEL=1 THEN NULL ELSE TRNPCTHFPMWFIJAVARIABLE END, TRNPCTCOMEOBS=CASE WHEN TRNPCEXCEL=1 THEN NULL ELSE TRNPCTCOMEOBS END, TRNPCEXCEL=NULL WHERE TRNPCAGRP=p_codigoAgrupado AND PERIDCCODI=P_PERICODI; END IF;
END IF;
IF(p_omitirCargaExcel=1) THEN
IF(NVL(p_codigoAgrupado,0)>0) THEN UPDATE TRN_POTENCIA_CONTRATADA SET TRNPCAGRP=null, TRNPCNUMORD=null, TRNPCCODICAS =null, TRNPCEXCEL=NULL WHERE TRNPCAGRP=p_codigoAgrupado AND PERIDCCODI=P_PERICODI; END IF;
END IF;
END;

DECLARE
p_TRNPCENVCODI                NUMBER:=null;
p_PERICODI                    number:=:PeriCodi;
p_TRNPCTUSERNAMEINS           VARCHAR2(300):=:TrnPctUsernameIns;
BEGIN
INSERT INTO TRN_POTENCIA_CONTRATADA_ENVIOS(PERICODI,TRNPCENVUSUARIO,TRNPCENVFECHARE,TRNPCENVHORARE,TRNPCENVESTADO)VALUES(p_PERICODI,p_TRNPCTUSERNAMEINS,SYSDATE,to_char(SYSDATE, 'hh24:mm:ss'),'ACT') returning TRNPCENVCODI into p_TRNPCENVCODI;
SELECT p_TRNPCENVCODI INTO :TrnPcEnvCodi from dual;
END;

DECLARE p_PERICODI number:=:PeriCodi; p_TRNPCTCODI number:=0; p_EMPRCODI number:=0; p_BARRNOMBRE VARCHAR2(1000):=''; p_coresocodiAux number := :CoresoCodi; p_coregecodiAux number := :CoregeCodi; p_codigoAgrupado NUMBER:=0; p_tipoCasoAgrupado varchar2(10):= :TrnnpcCodicas; p_TRNPCTTOTALMWFIJA NUMBER:=:TrnPctTotalMWFija; p_TRNPCTHPMWFIJA NUMBER:=:TrnPctHPMWFija; p_TRNPCTHFPMWFIJA NUMBER:=:TrnPctHFPMWFija; p_TRNPCTTOTALMWVARIABLE NUMBER:=:TrnPctTotalMWVariable; p_TRNPCTHPMWFIJAVARIABLE NUMBER:=:TrnPctHPMWFijaVariable; p_TRNPCTHFPMWFIJAVARIABLE NUMBER:=:TrnPctHFPMWFijaVariable; p_TRNPCTCOMEOBS VARCHAR2(1000):=:TrnPctComeObs; p_TRNPCTUSERNAMEINS VARCHAR2(300):=:TrnPctUsernameIns; p_TRNPCENVCODI NUMBER:=:TrnPcEnvCodi; BEGIN IF(p_tipoCasoAgrupado='AGRVTP') THEN SELECT( SELECT TRNPCAGRP FROM TRN_POTENCIA_CONTRATADA WHERE TRNPCAGRP IS NOT NULL AND CORESOCODI=p_coresocodiAux AND COREGECODI=p_coregecodiAux AND TRNPCESTADO='ACT' AND NVL(PERIDCCODI,PERICODI)=p_PERICODI ) INTO p_codigoAgrupado FROM DUAL; END IF;
IF(p_tipoCasoAgrupado='AGRVTA') THEN SELECT( SELECT TRNPCAGRP FROM TRN_POTENCIA_CONTRATADA WHERE TRNPCAGRP IS NOT NULL AND CORESOCODI=p_coresocodiAux AND TRNPCESTADO='ACT' AND COREGECODI IS NULL --- Solucion a data historica dasaji* AND NVL(PERIDCCODI,PERICODI)=p_PERICODI ) INTO p_codigoAgrupado from dual;
END IF;
IF(NVL(p_codigoAgrupado,0)>0) THEN UPDATE TRN_POTENCIA_CONTRATADA SET TRNPCTTOTALMWFIJA=p_TRNPCTTOTALMWFIJA, TRNPCTHPMWFIJA=p_TRNPCTHPMWFIJA, TRNPCTHFPMWFIJA=p_TRNPCTHFPMWFIJA, TRNPCTTOTALMWVARIABLE=p_TRNPCTTOTALMWVARIABLE, TRNPCTHPMWFIJAVARIABLE=p_TRNPCTHPMWFIJAVARIABLE, TRNPCTHFPMWFIJAVARIABLE=p_TRNPCTHFPMWFIJAVARIABLE, TRNPCTCOMEOBS=p_TRNPCTCOMEOBS, TRNPCEXCEL=1, TRNPCTUSERNAMEINS=p_TRNPCTUSERNAMEINS, TRNPCENVCODI=p_TRNPCENVCODI WHERE TRNPCAGRP=p_codigoAgrupado; END IF;
IF(NVL(p_codigoAgrupado,0)=0) THEN
IF(p_tipoCasoAgrupado='AGRVTA') THEN SELECT( SELECT TRNPCTCODI FROM TRN_POTENCIA_CONTRATADA WHERE CORESOCODI=p_coresocodiAux AND TRNPCESTADO='ACT' AND COREGECODI IS NULL --- Solucion a data historica dasaji* AND NVL(PERIDCCODI,PERICODI)=p_PERICODI ) INTO p_TRNPCTCODI FROM DUAL; END IF;
IF(p_tipoCasoAgrupado='AGRVTP') THEN SELECT( SELECT TRNPCTCODI FROM TRN_POTENCIA_CONTRATADA WHERE CORESOCODI=p_coresocodiAux AND COREGECODI=p_coregecodiAux AND TRNPCESTADO='ACT' --AND COREGECODI IS NULL AND NVL(PERIDCCODI,PERICODI)=p_PERICODI ) INTO p_TRNPCTCODI FROM DUAL; END IF;
UPDATE trn_codigo_retiro_solicitud SET TRNPCTIPOPOTENCIA =CASE WHEN p_tipoCasoAgrupado='AGRVTA' THEN 1 WHEN p_tipoCasoAgrupado='AGRVTP' THEN 2 END WHERE CORESOCODI=p_coresocodiAux;
IF(p_TRNPCTCODI is null) THEN
SELECT NVL(MAX(TRNPCTCODI),0)+1 INTO p_TRNPCTCODI FROM TRN_POTENCIA_CONTRATADA;
SELECT GENEMPRCODI ,BAR.BARRNOMBRE INTO p_EMPRCODI ,p_BARRNOMBRE FROM TRN_CODIGO_RETIRO_SOLICITUD RSL INNER JOIN TRN_BARRA BAR ON RSL.BARRCODI=BAR.BARRCODI WHERE coresocodi=p_coresocodiAux;
Insert into TRN_POTENCIA_CONTRATADA ( PERIDCCODI, EMPRCODI, CORESOCODI, TRNPCTCODI, TRNPCTPTOSUMINS, TRNPCTTOTALMWFIJA, TRNPCTHPMWFIJA, TRNPCTHFPMWFIJA, TRNPCTTOTALMWVARIABLE, TRNPCTHPMWFIJAVARIABLE, TRNPCTHFPMWFIJAVARIABLE, TRNPCTCOMEOBS, TRNPCTUSERNAMEINS, TRNPCTFECINS, COREGECODI, TRNPCAGRP, TRNPCNUMORD, TRNPCCODICAS, TRNPCESTADO, TRNPCENVCODI) values (p_PERICODI ,p_EMPRCODI ,p_coresocodiAux ,p_TRNPCTCODI ,p_BARRNOMBRE ,p_TRNPCTTOTALMWFIJA ,p_TRNPCTHPMWFIJA ,p_TRNPCTHFPMWFIJA ,p_TRNPCTTOTALMWVARIABLE ,p_TRNPCTHPMWFIJAVARIABLE ,p_TRNPCTHFPMWFIJAVARIABLE ,p_TRNPCTCOMEOBS ,p_TRNPCTUSERNAMEINS ,to_date(sysdate,'DD/MM/RR') ,CASE WHEN NVL(p_coregecodiAux,0)=0 THEN NULL ELSE p_coregecodiAux END ,null ,null ,null ,'ACT' ,p_TRNPCENVCODI); --END IF; elsif(p_TRNPCTCODI is not null) THEN UPDATE TRN_POTENCIA_CONTRATADA SET TRNPCTTOTALMWFIJA=p_TRNPCTTOTALMWFIJA, TRNPCTHPMWFIJA=p_TRNPCTHPMWFIJA, TRNPCTHFPMWFIJA=p_TRNPCTHFPMWFIJA, TRNPCTTOTALMWVARIABLE=p_TRNPCTTOTALMWVARIABLE, TRNPCTHPMWFIJAVARIABLE=p_TRNPCTHPMWFIJAVARIABLE, TRNPCTHFPMWFIJAVARIABLE=p_TRNPCTHFPMWFIJAVARIABLE, TRNPCTCOMEOBS=p_TRNPCTCOMEOBS, TRNPCENVCODI=p_TRNPCENVCODI, --TRNPCEXCEL=1, TRNPCTUSERNAMEINS=p_TRNPCTUSERNAMEINS, TRNPCESTADO='ACT' WHERE TRNPCTCODI=p_TRNPCTCODI; END IF; END IF;
END;

DECLARE p_PERICODI number:=:PeriCodi; p_TRNPCTCODI number:=0; p_EMPRCODI number:=0; p_BARRNOMBRE VARCHAR2(1000):=''; p_coresocodiAux number := :CoresoCodi; p_coregecodiAux number := :CoregeCodi; p_codigoAgrupado NUMBER:=0; p_tipoCasoAgrupado varchar2(10):= :TrnnpcCodicas; p_TRNPCTTOTALMWFIJA NUMBER:=:TrnPctTotalMWFija; p_TRNPCTHPMWFIJA NUMBER:=:TrnPctHPMWFija; p_TRNPCTHFPMWFIJA NUMBER:=:TrnPctHFPMWFija; p_TRNPCTTOTALMWVARIABLE NUMBER:=:TrnPctTotalMWVariable; p_TRNPCTHPMWFIJAVARIABLE NUMBER:=:TrnPctHPMWFijaVariable; p_TRNPCTHFPMWFIJAVARIABLE NUMBER:=:TrnPctHFPMWFijaVariable; p_TRNPCTCOMEOBS VARCHAR2(1000):=:TrnPctComeObs; p_TRNPCTUSERNAMEINS VARCHAR2(300):=:TrnPctUsernameIns; p_TRNPCENVCODI NUMBER:=:TrnPcEnvCodi; BEGIN
IF(p_tipoCasoAgrupado='AGRVTA') THEN SELECT( SELECT TRNPCTCODI FROM TRN_POTENCIA_CONTRATADA WHERE CORESOCODI=p_coresocodiAux AND TRNPCESTADO='ACT' AND COREGECODI IS NULL AND NVL(PERIDCCODI,PERICODI)=p_PERICODI ) INTO p_TRNPCTCODI FROM DUAL; END IF;
IF(p_tipoCasoAgrupado='AGRVTP') THEN SELECT( SELECT TRNPCTCODI FROM TRN_POTENCIA_CONTRATADA WHERE CORESOCODI=p_coresocodiAux AND COREGECODI=p_coregecodiAux AND NVL(PERIDCCODI,PERICODI)=p_PERICODI -- AND TRNPCESTADO='ACT' ) INTO p_TRNPCTCODI FROM DUAL; END IF;
UPDATE trn_codigo_retiro_solicitud SET TRNPCTIPOPOTENCIA =CASE WHEN p_tipoCasoAgrupado='AGRVTA' THEN 1 WHEN p_tipoCasoAgrupado='AGRVTP' THEN 2 END WHERE CORESOCODI=p_coresocodiAux;
--UPDATE trn_codigo_retiro_solicitud --SET CORESOESTAPR = 'PEN' --WHERE CORESOCODI=p_coresocodiAux AND CORESOESTADO='ACT';
IF(p_TRNPCTCODI is null) THEN
SELECT NVL(MAX(TRNPCTCODI),0)+1 INTO p_TRNPCTCODI FROM TRN_POTENCIA_CONTRATADA;
SELECT GENEMPRCODI ,BAR.BARRNOMBRE INTO p_EMPRCODI ,p_BARRNOMBRE FROM TRN_CODIGO_RETIRO_SOLICITUD RSL INNER JOIN TRN_BARRA BAR ON RSL.BARRCODI=BAR.BARRCODI WHERE coresocodi=p_coresocodiAux;
Insert into TRN_POTENCIA_CONTRATADA ( PERIDCCODI, EMPRCODI, CORESOCODI, TRNPCTCODI, TRNPCTPTOSUMINS, TRNPCTTOTALMWFIJA, TRNPCTHPMWFIJA, TRNPCTHFPMWFIJA, TRNPCTTOTALMWVARIABLE, TRNPCTHPMWFIJAVARIABLE, TRNPCTHFPMWFIJAVARIABLE, TRNPCTCOMEOBS, TRNPCTUSERNAMEINS, TRNPCTFECINS, COREGECODI, TRNPCAGRP, TRNPCNUMORD, TRNPCCODICAS, TRNPCESTADO, TRNPCENVCODI) values (p_PERICODI ,p_EMPRCODI ,p_coresocodiAux ,p_TRNPCTCODI ,p_BARRNOMBRE ,p_TRNPCTTOTALMWFIJA ,p_TRNPCTHPMWFIJA ,p_TRNPCTHFPMWFIJA ,p_TRNPCTTOTALMWVARIABLE ,p_TRNPCTHPMWFIJAVARIABLE ,p_TRNPCTHFPMWFIJAVARIABLE ,p_TRNPCTCOMEOBS ,p_TRNPCTUSERNAMEINS ,to_date(sysdate,'DD/MM/RR') ,CASE WHEN NVL(p_coregecodiAux,0)=0 THEN NULL ELSE p_coregecodiAux END ,null ,null ,null ,'ACT' ,p_TRNPCENVCODI); --END IF; elsif(p_TRNPCTCODI is not null) THEN UPDATE TRN_POTENCIA_CONTRATADA SET TRNPCTTOTALMWFIJA=p_TRNPCTTOTALMWFIJA, TRNPCTHPMWFIJA=p_TRNPCTHPMWFIJA, TRNPCTHFPMWFIJA=p_TRNPCTHFPMWFIJA, TRNPCTTOTALMWVARIABLE=p_TRNPCTTOTALMWVARIABLE, TRNPCTHPMWFIJAVARIABLE=p_TRNPCTHPMWFIJAVARIABLE, TRNPCTHFPMWFIJAVARIABLE=p_TRNPCTHFPMWFIJAVARIABLE, TRNPCTCOMEOBS=p_TRNPCTCOMEOBS, TRNPCENVCODI=p_TRNPCENVCODI, --TRNPCEXCEL=1, TRNPCTUSERNAMEINS=p_TRNPCTUSERNAMEINS, TRNPCESTADO='ACT' WHERE TRNPCTCODI=p_TRNPCTCODI; END IF;
END;

DECLARE p_PERICODI number:=:PeriCodi; p_TRNPCTCODI number:=0; p_EMPRCODI number:=0; p_BARRNOMBRE VARCHAR2(1000):=''; p_coresocodiAux number := :CoresoCodi; p_coregecodiAux number := :CoregeCodi; p_codigoAgrupado NUMBER:=0; p_tipoCasoAgrupado varchar2(10):= :TrnnpcCodicas; p_TRNPCTTOTALMWFIJA NUMBER:=:TrnPctTotalMWFija; p_TRNPCTHPMWFIJA NUMBER:=:TrnPctHPMWFija; p_TRNPCTHFPMWFIJA NUMBER:=:TrnPctHFPMWFija; p_TRNPCTTOTALMWVARIABLE NUMBER:=:TrnPctTotalMWVariable; p_TRNPCTHPMWFIJAVARIABLE NUMBER:=:TrnPctHPMWFijaVariable; p_TRNPCTHFPMWFIJAVARIABLE NUMBER:=:TrnPctHFPMWFijaVariable; p_TRNPCTCOMEOBS VARCHAR2(1000):=:TrnPctComeObs; p_TRNPCTUSERNAMEINS VARCHAR2(300):=:TrnPctUsernameIns; p_TRNPCENVCODI NUMBER:=:TrnPcEnvCodi; BEGIN
--UPDATE trn_codigo_retiro_solicitud --SET CORESOESTAPR='APR', COESUSERAPR=p_TRNPCTUSERNAMEINS, CORESOFECAPR=SYSDATE --WHERE CORESOCODI=p_coresocodiAux AND CORESOESTAPR='PEN';
IF(p_tipoCasoAgrupado='AGRVTA') THEN SELECT( SELECT TRNPCTCODI FROM TRN_POTENCIA_CONTRATADA_APR WHERE CORESOCODI=p_coresocodiAux AND TRNPCESTADO='ACT' AND COREGECODI IS NULL AND NVL(PERIDCCODI,PERICODI)=p_PERICODI ) INTO p_TRNPCTCODI FROM DUAL; END IF;
IF(p_tipoCasoAgrupado='AGRVTP') THEN SELECT( SELECT TRNPCTCODI FROM TRN_POTENCIA_CONTRATADA_APR WHERE CORESOCODI=p_coresocodiAux AND COREGECODI=p_coregecodiAux AND NVL(PERIDCCODI,PERICODI)=p_PERICODI -- AND TRNPCESTADO='ACT' ) INTO p_TRNPCTCODI FROM DUAL; END IF;
--UPDATE trn_codigo_retiro_solicitud --SET TRNPCTIPOPOTENCIA =CASE WHEN p_tipoCasoAgrupado='AGRVTA' THEN 1 WHEN p_tipoCasoAgrupado='AGRVTP' THEN 2 END --, CORESOESTAPR = 'PEN', CORESOESTAPR='APR', COESUSERAPR=p_TRNPCTUSERNAMEINS, CORESOFECAPR=SYSDATE --WHERE CORESOCODI=p_coresocodiAux;
IF(p_TRNPCTCODI is null) THEN
SELECT NVL(MAX(TRNPCTCODI),0)+1 INTO p_TRNPCTCODI FROM TRN_POTENCIA_CONTRATADA_APR;
SELECT GENEMPRCODI ,BAR.BARRNOMBRE INTO p_EMPRCODI ,p_BARRNOMBRE FROM TRN_CODIGO_RETIRO_SOLICITUD RSL INNER JOIN TRN_BARRA BAR ON RSL.BARRCODI=BAR.BARRCODI WHERE coresocodi=p_coresocodiAux;
UPDATE trn_codigo_retiro_solicitud SET CORESOESTAPR = 'PEN' WHERE CORESOCODI=p_coresocodiAux AND CORESOESTADO='ACT';
Insert into TRN_POTENCIA_CONTRATADA_APR ( PERIDCCODI, EMPRCODI, CORESOCODI, TRNPCTCODI, TRNPCTPTOSUMINS, TRNPCTTOTALMWFIJA, TRNPCTHPMWFIJA, TRNPCTHFPMWFIJA, TRNPCTTOTALMWVARIABLE, TRNPCTHPMWFIJAVARIABLE, TRNPCTHFPMWFIJAVARIABLE, TRNPCTCOMEOBS, TRNPCTUSERNAMEINS, TRNPCTFECINS, COREGECODI, TRNPCAGRP, TRNPCNUMORD, TRNPCCODICAS, TRNPCESTADO, TRNPCENVCODI) values (p_PERICODI ,p_EMPRCODI ,p_coresocodiAux ,p_TRNPCTCODI ,p_BARRNOMBRE ,p_TRNPCTTOTALMWFIJA ,p_TRNPCTHPMWFIJA ,p_TRNPCTHFPMWFIJA ,p_TRNPCTTOTALMWVARIABLE ,p_TRNPCTHPMWFIJAVARIABLE ,p_TRNPCTHFPMWFIJAVARIABLE ,p_TRNPCTCOMEOBS ,p_TRNPCTUSERNAMEINS ,to_date(sysdate,'DD/MM/RR') ,CASE WHEN NVL(p_coregecodiAux,0)=0 THEN NULL ELSE p_coregecodiAux END ,null ,null ,null ,'ACT' ,p_TRNPCENVCODI); --END IF; elsif(p_TRNPCTCODI is not null) THEN
UPDATE trn_codigo_retiro_solicitud SET CORESOESTAPR = 'PEN' WHERE CORESOCODI=p_coresocodiAux AND CORESOESTADO='ACT';
UPDATE TRN_POTENCIA_CONTRATADA_APR SET TRNPCTTOTALMWFIJA=p_TRNPCTTOTALMWFIJA, TRNPCTHPMWFIJA=p_TRNPCTHPMWFIJA, TRNPCTHFPMWFIJA=p_TRNPCTHFPMWFIJA, TRNPCTTOTALMWVARIABLE=p_TRNPCTTOTALMWVARIABLE, TRNPCTHPMWFIJAVARIABLE=p_TRNPCTHPMWFIJAVARIABLE, TRNPCTHFPMWFIJAVARIABLE=p_TRNPCTHFPMWFIJAVARIABLE, TRNPCTCOMEOBS=p_TRNPCTCOMEOBS, TRNPCENVCODI=p_TRNPCENVCODI, --TRNPCEXCEL=1, TRNPCTUSERNAMEINS=p_TRNPCTUSERNAMEINS, TRNPCESTADO='ACT' WHERE TRNPCTCODI=p_TRNPCTCODI; END IF;
END;
